package fabric.common;

/**
 * This encapsulates the version of Fabric. DO NOT EDIT THIS CLASS BY HAND! This
 * is automatically generated by Ant. If you wish to change the version number,
 * run one of the following: ant bump-version (Bumps the version number.) ant
 * bump-major (Bumps the major version number.) ant bump-minor (Bumps the minor
 * version number.) ant bump-patch (Bumps the patch level.)
 */
public class Version {
  private int major = 0;
  private int minor = 1;
  private int patch = 0;

  public int major() {
    return major;
  }

  public int minor() {
    return minor;
  }

  public int patch() {
    return patch;
  }

  @Override
  public String toString() {
    return "0.1.0 (2010-08-30 19:13:38)";
  }
}
